/*
* 标签组件: label
* @params:
*       cls: label样式
*       labelwidth: fieldlabel宽度，默认为200px
*/

require('./label.scss');
var tpl = require('./label.jade');

/*
* mixin
*/
var BaseMixin = require('../../mixin/base');

var Label = Vue.extend({
    mixins: [BaseMixin],
    props: ['cls', 'fieldlabel', 'hidelabel', 'required', 'labelwidth', 'labeltext'],
    template: tpl(),
    data: function(){
        return {
            isrequired: this.required || false,
            labelstyle: {
                width: this.transformCssUnit(this.labelwidth || '200px')
            }
        }
    }
});

module.exports = Vue.component('vlabel', Label);